import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { FaCoffee } from 'react-icons/fa';
import { createReview } from "../../store/reviews";
import ShopReviewFeed from "./ShopReviewFeed";
import { fetchReviewsForShop, deleteReview } from "../../store/reviews";
import Stars from "./Stars";
import Map from "../Map/Index";
import CheckIn from '../CheckIn/CheckIn'
import EditReview from "./EditReview";

export const formatDateAndTime = (date) => {
  const time = new Date(date).toLocaleTimeString('en');

  const arr = date.split('-');
  const monthAndDay = `${arr[1]}/${arr[2].slice(0,2)}/${arr[0]}`

  return `${monthAndDay} ${time}`;

  }



const ShopDetails = () => {
  const {id} = useParams();
  const [rating, setRating] = useState(0);
  const [showReview, setShowReview] = useState(false);
  const [hoverRating, setHoverRating] = useState(undefined);
  const [comment, setComment] = useState('');
  const [image, setImage] = useState(null);
  const [errors, setErrors] = useState([]);

  const [count, setCount] = useState(0);
  const dispatch = useDispatch();
  const reviews = useSelector(state =>  state.reviews[id]);

  const [rating2, setRating2] = useState(0);
  // const [comment, setComment] = useState('');
  // const [image, setImage] = useState(null);

  // const handleMouseOver = value => {
  //   setHoverRating(value);
  // }
  const currentUser = useSelector(state => state.session.user);
  const visited = useSelector(state => state.shops.find(shop => shop.id == id));
  const handleRating = (i) => setRating(i);

  const [showEdit, setShowEdit] = useState(false);
  const [reviewId, setReviewId] = useState(null);

  const handleDelete = (reviewId) => {

    let answer = window.confirm("Confirm to delete post.");
    if (answer){
      dispatch(deleteReview(reviewId, parseInt(id)))
    }else return;

  }
  useEffect(() => {
    window.scrollTo(0, 0)
  }, []);

  const handleMouseOver = value => {
    setHoverRating(value);
  }
  const handleMouseLeave = () => {
    setHoverRating(undefined);
  }


  const cups = [1, 2, 3, 4, 5];

  const colors = {gold: '#A68A5B', grey: '#1A0A40 '}
  const styles = {
    cups: {

     }
   }

   const updateFile = (e) => {
    const file = e.target.files[0];
    if (file) setImage(file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setCount(prev => prev + 1);
    let newErrors = [];
    if (rating <= 0 ){
      newErrors.push('You must provide a score.')
      setErrors(newErrors)
    }
    const userId = currentUser.id;
    const shopId = id;
    let body;

    if (image && image.name) {
      body = {image, comment, rating, userId, shopId}
    }else {
     body = {comment, rating, userId, shopId}
    }

    dispatch(createReview(body))
      .then(() => {
        setComment("");
        setRating(0);
        setImage(null);

      }).then(dispatch(fetchReviewsForShop(id)))
      .catch(async (res) => {
        console.log(res)
      });
      if (!newErrors.length) {
        setRating(0);
        setShowReview(false);
      }
  };

  useEffect(()=> {
    setErrors([]);
  }, [rating])


   const handleComment = ({target}) => {setComment(target.value)}


  return (
    <div className='shop-details'>
      <h1 className='shop-name'>{visited?.name}</h1>

      <div className='review-feed-container'>
        <h2>Reviews</h2>
       { reviews?.length && reviews.map((review) =>
       <div className='individual-review' >
         {currentUser?.id === review.User?.id && <>
         <span className='edit-review' onClick={()=> { setShowEdit(!showEdit);
        setReviewId(review.id)}}>edit</span>
          <span className='delete-review' onClick={() => handleDelete(review.id)}>delete</span></>
        }
         <span className='review-author'>-{review.User?.username}</span>
         <span className='review-date'>{formatDateAndTime(review.createdAt)}</span>
        <p>{review.comment}</p>
       <div className='review-image-container'>

        { review.image ? <img className='review-image' src={review?.image}></img> : <img className='review-image' src={'data:image/jpeg;base64,'}></img> }

        </div>

        <Stars rating={review.rating}/>
       </div>

          ) }
      </div>



     { showEdit && <EditReview reviewId={reviewId} setShowEdit={setShowEdit}/> }

   { visited && <Map shop={visited} />}
    <span className='map-address'> {visited?.address}, {visited?.city}</span>

     { currentUser ? <> <CheckIn user={currentUser} id={id}/>
     <span  className='show-review'
     onClick={()=> {setShowReview(!showReview); setComment('')}}>
        {(showReview) ? 'cancel' : 'leave a review ?'}</span> </>

       :  <span className='show-review' style={{cursor: "default"}}>login to review</span> }

      { showReview &&  <form className='review' onSubmit={handleSubmit} >
        <h3 className='review-title'>Review</h3>
       {errors.length > 0 && <div className="review-errors">
        {  errors?.map(err => (<p>{err}</p>) )}
       </div> }
      <label className='upload-img'><i class="far fa-image"> <span className='img-label'> {image?.name ? image.name.slice(0,10) + image.name.slice(image.name.length -4) : "upload image" }</span></i>
          <input type="file" onChange={updateFile} />

       { image?.name && <span className='remove-img' onClick={(e)=>{ e.preventDefault(); setImage(null)}}> remove</span>}
        </label>

        <div className='rating' style={styles.cups}>
          {cups.map((each, i) => { return (
            <FaCoffee key={i}
            size={50}
            className='rating-cup'
            color={( hoverRating || rating ) > i ? colors.gold : colors.grey}
            onClick={() => handleRating(i + 1)}
            onMouseOver={() => handleMouseOver(i + 1)}
            onMouseLeave={()=> handleMouseLeave()}
            />)
          })}
        <span className='show-rating'>{rating}/5</span>
        </div>
        <textarea onChange={handleComment}  placeholder="How was your visit?" />
        <button className='submit-review' type='submit'>Submit</button>
        </form> }
    </div>

  )

}


export default ShopDetails;
